import axios from "axios";
import { url } from './../constants/auth'

export const Login = async (username, password) => {
    try {
        const data = await axios.post(url + "/auth/login", {
            username,
            password
        });
        if (!data.data.token) {
            return false;
        } else {
            return true;
        }
    } catch (error) {
        if (error) {
            return false;
        }
    }
}
