import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "./login.css";
import { Login } from './../services/authserivce'
export const LoginPage = (props) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [success, setSuccess] = useState(false);

  function validateForm() {
    return username.length > 0 && password.length > 0;
  }

  function Loginhandler(username, password) {
    setSuccess(Login(username, password))
    console.log(success)
    if (success) {
      props.history.push('/home')
      console.log(props.history)
    }

  }

  return (
    <div className="Login">
      <Form >
        <Form.Group size="lg" controlId="username">
          <Form.Label>Username</Form.Label>
          <Form.Control
            autoFocus
            type="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </Form.Group>
        <Form.Group size="lg" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <Button block size="lg" onClick={() => { Loginhandler(username, password) }} disabled={!validateForm()}>
          Login
        </Button>
        {success && <span style={{ color: 'red' }} disabled={success}>Inavlid credentials</span>
        }
      </Form>
    </div>
  );
}

export default LoginPage
